mapper.select:
SELECT item_id AS id, item_name AS name, item_code AS code, quantity, location, in_date AS inDate, out_date AS outDate, note, category FROM inventory_tbl WHERE 1=1
AND (item_name LIKE CONCAT('%', #{search}, '%') OR item_code LIKE CONCAT('%', #{search}, '%'))
AND product_type = #{productType}
AND category = #{category}
AND delivery_status = #{status}
AND in_date >= DATE_SUB(CURDATE(), INTERVAL #{regDays} DAY)
ORDER BY item_id DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM inventory_tbl
WHERE 1=1

    AND (item_name LIKE CONCAT('%', #{search}, '%')
    OR  item_code LIKE CONCAT('%', #{search}, '%'))


    AND product_type = #{productType}


    AND category = #{category}


    AND delivery_status = #{status}


    AND in_date >= DATE_SUB(CURDATE(), INTERVAL #{regDays} DAY)
